          var yearMonthNum = val.year + val.month/100;
          var currentYearMonthNum = this.calendarView.today().year + this.calendarView.today().month/100;

          var yearMonthNumDiff = yearMonthNum - currentYearMonthNum;

          if (yearMonthNumDiff<0) {
              this.setAttribute("relation", "past");
          } else if (yearMonthNumDiff==0) {
              this.setAttribute("relation", "today");
          } else {
              this.setAttribute("relation", "future");
