          var monthLabelCenter = document.getAnonymousElementByAttribute(this, 'anonid', 'GanttView-monthLabelCenter');

          this.setAttribute("style","min-width: " + val + "px; " + "max-width: " + val + "px;");			

          if (this.boxObject.width < labelWidth*5) {
						monthLabelCenter.setAttribute("hidden", "true")
						if (this.boxObject.width < labelWidth*3) {
							monthLabelRight.setAttribute("hidden", "true");
								if (this.boxObject.width < labelWidth*1) {
									monthLabelLeft.setAttribute("crop", "end");
								} else {
									monthLabelLeft.removeAttribute("crop");
								}
						} else {
							monthLabelRight.setAttribute("hidden", "false");
						}

					} else {
